  void _filterItems(String query) {
    // Filter items based on the query
    setState(() {
      filteredItems = items
          .where((item) =>
              item.title.toLowerCase().contains(query.toLowerCase()) ||
              item.name.toLowerCase().contains(query.toLowerCase()))
          .toList();
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        iconTheme: IconThemeData(color: Colors.white),
        backgroundColor: Color.fromARGB(255, 97, 135, 110),
        title: Text(
          'സമസ്ത നേതാക്കൾ',
          style: TextStyle(
            fontSize: 16.0,
            fontWeight: FontWeight.bold,
            color: Colors.white,
          ),
        ),
        actions: <Widget>[
          // Toggle between search icon and text field
          Padding(
            padding: const EdgeInsets.all(8.0),
            child: isSearching
                ? Container(
                    width: 300.0,
                    child: TextField(
                      onChanged: _filterItems,
                      decoration: InputDecoration(
                        hintText: 'search',
                        hintStyle: TextStyle(color: Colors.white),
                      ),
                    ),
                  )
                : IconButton(
                    onPressed: () {
                      setState(() {
                        isSearching = true;
                      });
                    },
                    icon: Icon(Icons.search,color: Colors.white,),
                  ),
          ),
        ],
      ),
      body: SingleChildScrollView(
        child: Column(
          children: [
            // Display filtered cardWidgets
            for (int i = 0; i < filteredItems.length; i += 3)
              Row(
                children: [
                  if (i < filteredItems.length)
                    cardWidget(sample: filteredItems[i]),
                  if (i + 1 < filteredItems.length)
                    cardWidget(sample: filteredItems[i + 1]),
                  if (i + 2 < filteredItems.length)
                    cardWidget(sample: filteredItems[i + 2]),
                  // Fill empty spaces with SizedBox
                  if (i + 1 >= filteredItems.length) SizedBox(width: 120.0),
                  if (i + 2 >= filteredItems.length) SizedBox(width: 140.0),
                ],
              ),
          ],
        ),
      ),
    );
  }
